import { Component } from "react";
import { fetchPosts } from "./api/posts";
import { PostContainer } from "./components/PostContainer";

// 1.დაფეტჩეთ https://jsonplaceholder.typicode.com/posts 
// 2.გამოიტანეთ ყველა პოსტის title,body + <button> see comments
// 3. button ზე დაკლიკებისას სადმე დომში გამოიტანეთ ამ პოსტის კომენტარები
// https://jsonplaceholder.typicode.com/posts/1/comments        ეეს ლინკი წამოიღებს იმ პოსტის კომენტარებს რომლის id არის 1 . როცა პოსტს დავაკლიკებ მოთხოვნა უნდა გაიგზავნოს შესაბამისი პოსტის იდ -ით მაგ ლინკზე და გამოიტანოთ მომხმარებლისთვის


class AppShowInstruction extends Component {
	constructor(){
		super();
		this.state = {
			posts: []
		};
		
	}
	componentDidMount(){
		fetchPosts().then(posts => this.setState({posts}));
	}
	render() {
		const { posts } = this.state;
		return (
			<div>
				<PostContainer posts={posts} />
			</div>
		);
	}
}



export default AppShowInstruction;
