import { Component } from "react";

export class CocktailContainer extends Component {
	constructor(props) {
		super(props);
	}

	render() {
		const { cocktail, title, onShowInstruction } = this.props;
		const { strDrink, strDrinkThumb, strInstructions, idDrink } = cocktail;

		return (
			<article className="cocktail-container" >
				<h2>{strDrink}</h2>
				<img src={strDrinkThumb} alt={title} />
				<p></p>
				<button onClick={(e) => onShowInstruction(idDrink)}>See Instruction</button>
			</article>
		);
	}
}
