import { Component } from "react";
import { fetchposts } from "./api/posts";
import { CocktailContainer } from "./components/CocktailContainer";

class AppShowInstruction extends Component {
	constructor() {
		super();
		this.state = {
			posts: {
				isLoading: true,
				isLoaded: false,
				isError: false,
			},
		};
		this.handleShowInstriction = this.handleShowInstriction.bind(this);
	}

	async componentDidMount() {
		try {
			const posts = await fetchposts();
			this.setState({
				posts: {
					data: posts,
					isLoading: false,
					isLoaded: true,
					isError: false,
				},
			});
		} catch (err) {
			this.setState({
				posts: {
					data: [],
					isLoading: false,
					isLoaded: true,
					isError: true,
				},
			});
		}
	}

	handleShowInstriction(id) {
	//  return	<p>{posts.str}</p>
	}

	render() {
		const { posts } = this.state;

		if (posts.isLoading) {
			return <h1>იტვირთება...</h1>;
		}

		if (posts.isError) {
			return <h1>შეცდომა...</h1>;
		}

		return (
			<section className="posts-container">
				{posts.data.map((cocktail) => (
					<CocktailContainer
						onShowInstruction={this.handleShowInstriction}
						cocktail={cocktail}
						key={cocktail.idDrink}
					/>
				))}
			</section>
		);
	}
}

