import { Component } from "react";

export class PostContainer extends Component {
	constructor(props) {
		super(props);
	}

	render() {
		const { posts , onClick} = this.props;

		return (
			<article className="post-container" >
                <h2>{posts.title}</h2>
                <p>{posts.body}</p>
                <button onClick={(e)=> onClick(posts.id)} >See comments</button>
				
			</article>
		);
	}
}
